import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [state, setState] = useState({
    todoList: [],
    activeItem: {
      id: null,
      title: "",
      completed: false,
    },
    editing: false,
  });

  const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value;
    setState({
      ...state,
      activeItem: { ...state.activeItem, title: value },
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    async function submitTask() {
      let response = await fetch("http://127.0.0.1:8000/api/task-create/", {
        method: "POST",
        body: JSON.stringify(state.activeItem),
      });
    }
    submitTask();
    setState({
      ...state,
      activeItem: {
        id: null,
        title: "",
        completed: false,
      },
    });
  };

  useEffect(() => {
    console.log("Fetching");
    async function fetchTasks() {
      let response = await fetch("http://127.0.0.1:8000/api/task-list/");
      let data = await response.json();
      setState({ ...state, todoList: data });
    }
    fetchTasks();
  }, [state.activeItem]);

  // console.log(state);

  return (
    <div className="container">
      <div className="task-container">
        <div id="form-wrapper">
          <form id="form">
            <div className="flex-wrapper">
              <div style={{ flex: 6 }}>
                <input
                  className="form-control"
                  id="title"
                  name="title"
                  placeholder="Title"
                  type="text"
                  onChange={handleChange}
                />
              </div>
              <div style={{ flex: 1 }}>
                <input
                  className="btn btn-warning"
                  id="submit"
                  name="Add"
                  type="submit"
                  onSubmit={handleSubmit}
                />
              </div>
            </div>
          </form>
          <div className="list-wrapper">
            {state.todoList.length &&
              state.todoList.map((task) => (
                <div key={task.id} className="task-wrapper">
                  <div style={{ flex: 7 }}>{task.title}</div>
                  <div style={{ flex: 1 }}>
                    <button className="btn btn-success">Edit</button>
                  </div>
                  <div style={{ flex: 1 }}>
                    <button className="btn btn-danger">Delete</button>
                  </div>
                </div>
              ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
