import { createContext, useState, useEffect } from "react";
// JWT
import jwtDecode from "jwt-decode";
// Router
import { useNavigate } from "react-router";

const AuthContext = createContext();
export default AuthContext;

export const AuthProvider = ({ children }) => {
  let [tokens, setTokens] = useState(() =>
    localStorage.getItem("authTokens")
      ? JSON.parse(localStorage.getItem("authTokens"))
      : null
  );
  let [user, setUser] = useState(() =>
    localStorage.getItem("authTokens")
      ? JSON.parse(localStorage.getItem("authTokens").user)
      : null
  );

  let [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  let loginUser = async (e) => {
    e.preventDefault();
    let response = await fetch("http://127.0.0.1:8000/api/auth/login/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: e.target.username.value,
        password: e.target.password.value,
      }),
    });
    let data = await response.json();
    // let { key } = await response.json();
    // Status 200 === "OK"
    if (response.status === 200) {
      let tokens = {
        access: data.access_token,
        refresh: data.refresh_token,
        user: data.user,
      };
      setTokens(tokens);
      setUser(data.user);
      localStorage.setItem("authTokens", JSON.stringify(tokens));
      navigate("/");
    } else {
      alert("Login error!");
    }
  };

  let logoutUser = () => {
    setTokens(null);
    setUser(null);
    localStorage.removeItem("authTokens");
  };

  let registerUser = async (e) => {
    e.preventDefault();
    let username = e.currentTarget.username.value;
    let password = e.currentTarget.password.value;
    let confirm = e.currentTarget.confirm.value;
    if (password !== confirm) {
      alert("Passwords do not match!");
      e.currentTarget.password.value = "";
      e.currentTarget.confirm.value = "";
    } else {
      console.log("Registering new user!");
      try {
        let response = await fetch("http://127.0.0.1:8000/api/auth/register/", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            username: username,
            password1: password,
            password2: confirm,
          }),
        });
        let data = await response.json();
        console.log(data);
      } catch (error) {
        console.log(error, "ERROR!");
      }
    }
  };

  // let updateToken = async () => {
  //   console.log("Updating token!");
  //   let response = await fetch("http://127.0.0.1:8000/api/token/refresh/", {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify({ refresh: authTokens.refresh }),
  //   });
  //   let data = await response.json();

  //   if (response.status === 200) {
  //     console.log("Token refreshed!");
  //     setAuthTokens({ ...authTokens, access: data.access });
  //     localStorage.setItem(
  //       "authTokens",
  //       JSON.stringify({ ...authTokens, access: data.access })
  //     );
  //   } else logoutUser();
  // };

  // useEffect(() => {
  //   let minutes = 1000 * 60 * 4;
  //   let interval = setInterval(() => {
  //     if (authTokens) {
  //       updateToken();
  //     }
  //   }, minutes);
  //   return () => clearInterval(interval);
  // }, [authTokens, loading]);

  let contextData = {
    user: user,
    tokens: tokens,
    loginUser: loginUser,
    logoutUser: logoutUser,
    registerUser: registerUser,
  };

  return (
    <AuthContext.Provider value={contextData}>{children}</AuthContext.Provider>
  );
};
