import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [state, setState] = useState({
    todoList: [],
    activeItem: {
      id: null,
      title: "",
      completed: false,
    },
    editing: false,
  });

  const handleFormInput = (e) => {
    // let name = e.target.name;
    let value = e.target.value;
    setState({
      ...state,
      activeItem: { ...state.activeItem, title: value },
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    var url = "http://127.0.0.1:8000/api/task-create/";
    if (state.editing === true) {
      url = `http://127.0.0.1:8000/api/task-update/${state.activeItem.id}/`;
    }
    const csrftoken = getCookie("csrftoken");
    async function submitTask() {
      try {
        let response = await fetch(url, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrftoken,
          },
          body: JSON.stringify(state.activeItem),
        });
        let data = response.json();
        setState({
          ...state,
          todoList: [...state.todoList, data],
          // Reset the activeItem object
          activeItem: {
            id: null,
            title: "",
            completed: false,
          },
          editing: false,
        });
        console.log(state, "here");
      } catch {
        console.log("ERROR!");
      }
    }
    submitTask();
  };

  const startEdit = (task) => {
    setState({ ...state, activeItem: task, editing: true });
  };

  const handleDelete = (id) => {
    const deleteTask = async () => {
      try {
        let response = await fetch(
          `http://127.0.0.1:8000/api/task-delete/${id}/`,
          {
            method: "DELETE",
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
        fetchTasks();
      } catch {
        console.log("ERROR!");
      }
    };
    deleteTask();
  };

  const handleCompleted = (task) => {
    task.completed = !task.completed;
    const completeTask = async () => {
      try {
        let response = await fetch(
          `http://127.0.0.1:8000/api/task-update/${task.id}/`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(task),
          }
        );
        fetchTasks();
      } catch {
        console.log("ERROR updating task completion status!");
      }
    };
    completeTask();
  };

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== "") {
      const cookies = document.cookie.split(";");
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        // Does this cookie string begin with the name we want?
        if (cookie.substring(0, name.length + 1) === name + "=") {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  }

  async function fetchTasks() {
    let response = await fetch("http://127.0.0.1:8000/api/task-list/");
    let data = await response.json();
    setState({ ...state, todoList: data });
  }

  useEffect(() => {
    console.log("Fetching");
    fetchTasks();
  }, [state.editing]);

  return (
    <div className="container">
      <div className="task-container">
        <div id="form-wrapper">
          <form id="form" onSubmit={handleSubmit}>
            <div className="flex-wrapper">
              <div style={{ flex: 6 }}>
                <input
                  className="form-control"
                  id="title"
                  name="title"
                  onChange={handleFormInput}
                  placeholder="Title"
                  type="text"
                  value={state.activeItem.title}
                />
              </div>
              <div style={{ flex: 1 }}>
                <input
                  className="btn btn-warning"
                  id="submit"
                  name="Add"
                  type="submit"
                />
              </div>
            </div>
          </form>
          <div className="list-wrapper">
            {state.todoList.length &&
              state.todoList.map((task) => (
                <div key={task.id} className="task-wrapper">
                  <div
                    className={task.completed ? "completed" : null}
                    style={{ flex: 7 }}
                    onClick={() => handleCompleted(task)}
                  >
                    {task.title}
                  </div>
                  <div style={{ flex: 1 }}>
                    <button
                      className="btn btn-success"
                      onClick={() => startEdit(task)}
                    >
                      Edit
                    </button>
                  </div>
                  <div style={{ flex: 1 }}>
                    <button
                      className="btn btn-danger"
                      onClick={() => handleDelete(task.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
